import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import Webcam from 'react-webcam';
import { motion } from 'framer-motion';
import { 
  Camera, 
  Mic, 
  MicOff, 
  Brain, 
  BarChart3, 
  AlertTriangle,
  CheckCircle,
  X,
  Play,
  Pause,
  SkipForward,
  SkipBack,
  Square
} from 'lucide-react';
import { useInterview } from '../context/InterviewContext';
import toast from 'react-hot-toast';

const InterviewRoom = () => {
  const navigate = useNavigate();
  const { 
    sessionId, 
    candidateName, 
    questions, 
    currentQuestionIndex,
    metrics,
    api,
    utils,
    dispatch,
    isLoading,
    isInterviewActive,
    isInterviewCompleted,
    isConnected,
    socket,
    resumeAnalysis
  } = useInterview();

  const webcamRef = useRef(null);
  const [isVideoEnabled, setIsVideoEnabled] = useState(true);
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const [interviewTime, setInterviewTime] = useState(0);
  const [isPaused, setIsPaused] = useState(false);
  const [showWarnings, setShowWarnings] = useState(true);
  const [warningMessages, setWarningMessages] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const [currentAnswer, setCurrentAnswer] = useState('');
  const [showAnswerInput, setShowAnswerInput] = useState(false);
  const [followupQuestion, setFollowupQuestion] = useState(null);
  const [isSubmittingAnswer, setIsSubmittingAnswer] = useState(false);
  const [eyeContactStatus, setEyeContactStatus] = useState(null);
  const [eyeContactWarning, setEyeContactWarning] = useState(null);
  const [eyeContactTimer, setEyeContactTimer] = useState(0);
  const [resumeFile, setResumeFile] = useState(null);
  const [isAnalyzingResume, setIsAnalyzingResume] = useState(false);
  const [showResumeAnalysis, setShowResumeAnalysis] = useState(false);

  // Check if we have session data
  useEffect(() => {
    if (!sessionId || !questions.length) {
      navigate('/setup');
      return;
    }
  }, [sessionId, questions, navigate]);

  // Start interview timer
  useEffect(() => {
    if (isInterviewActive && !isPaused) {
      const timer = setInterval(() => {
        setInterviewTime(prev => prev + 1);
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [isInterviewActive, isPaused]);

  // Process video frames
  const processFrame = useCallback(async () => {
    if (webcamRef.current && isVideoEnabled && isInterviewActive) {
      const imageSrc = webcamRef.current.getScreenshot();
      if (imageSrc) {
        setIsProcessing(true);
        try {
          console.log('Processing frame...'); // Debug log
          const result = await api.processFrame(imageSrc);
          console.log('Frame processing result:', result); // Debug log
          
          // If frame processing doesn't update metrics automatically, fetch them
          if (result && result.success && !result.metrics) {
            console.log('Fetching updated metrics...'); // Debug log
            await api.getCurrentMetrics(sessionId);
          }
        } catch (error) {
          console.error('Frame processing failed:', error);
        } finally {
          setIsProcessing(false);
        }
      }
    }
  }, [api, isVideoEnabled, isInterviewActive, sessionId]);

  // Process frames at regular intervals
  useEffect(() => {
    const interval = setInterval(processFrame, 2000); // Process every 2 seconds for better performance
    return () => clearInterval(interval);
  }, [processFrame]);

  // Test face detection manually
  const testFaceDetection = async () => {
    if (webcamRef.current && isVideoEnabled) {
      const imageSrc = webcamRef.current.getScreenshot();
      if (imageSrc) {
        console.log('Manual face detection test...'); // Debug log
        setIsProcessing(true);
        try {
          const result = await api.processFrame(imageSrc);
          console.log('Manual test result:', result); // Debug log
          
          // Force refresh metrics
          await api.getCurrentMetrics(sessionId);
        } catch (error) {
          console.error('Manual test failed:', error);
        } finally {
          setIsProcessing(false);
        }
      }
    }
  };

  // Submit answer and get follow-up question
  const submitAnswer = async () => {
    if (!currentAnswer.trim() || !currentQuestion) return;

    setIsSubmittingAnswer(true);
    try {
      const result = await api.submitAnswer(
        currentQuestionIndex,
        currentAnswer,
        currentQuestion.question
      );

      if (result.success) {
        // Show follow-up question
        setFollowupQuestion(result.followup_question);
        setCurrentAnswer('');
        setShowAnswerInput(false);
        
        // Show feedback
        if (result.feedback && result.feedback.length > 0) {
          toast.success(`Answer submitted! ${result.feedback[0]}`);
        } else {
          toast.success('Answer submitted successfully!');
        }
      }
    } catch (error) {
      toast.error('Failed to submit answer');
      console.error('Answer submission error:', error);
    } finally {
      setIsSubmittingAnswer(false);
    }
  };

  // Skip to next question
  const nextQuestion = () => {
    if (followupQuestion) {
      // Add follow-up question to the questions list
      utils.nextQuestion();
      setFollowupQuestion(null);
    } else {
      utils.nextQuestion();
    }
  };

  // Monitor eye contact in real-time
  useEffect(() => {
    if (!socket || !sessionId) return;

    const handleEyeContactUpdate = (data) => {
      if (data.session_id === sessionId) {
        setEyeContactStatus(data.status);
        
        if (data.status.warning_active) {
          setEyeContactWarning(data.status.warning_message);
          // Show warning toast
          if (data.status.warning_level === 'high') {
            toast.error(data.status.warning_message, { duration: 5000 });
          } else {
            toast.warning(data.status.warning_message, { duration: 3000 });
          }
        } else {
          setEyeContactWarning(null);
        }
      }
    };

    const handleError = (data) => {
      console.error('Socket error:', data.message);
    };

    socket.on('eye_contact_update', handleEyeContactUpdate);
    socket.on('error', handleError);

    // Request initial eye contact status
    api.requestEyeContactStatus();

    // Set up periodic eye contact status requests
    const interval = setInterval(() => {
      api.requestEyeContactStatus();
    }, 2000); // Check every 2 seconds

    return () => {
      socket.off('eye_contact_update', handleEyeContactUpdate);
      socket.off('error', handleError);
      clearInterval(interval);
    };
  }, [socket, sessionId, api]);

  // Update eye contact timer
  useEffect(() => {
    if (!eyeContactStatus) return;

    const interval = setInterval(() => {
      if (!eyeContactStatus.is_looking) {
        setEyeContactTimer(prev => prev + 1);
      } else {
        setEyeContactTimer(0);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [eyeContactStatus]);

  // Handle resume file upload
  const handleResumeUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setResumeFile(file);
    }
  };

  // Analyze resume
  const analyzeResume = async () => {
    if (!resumeFile) return;

    setIsAnalyzingResume(true);
    try {
      const result = await api.analyzeResume(resumeFile);
      if (result.success) {
        setShowResumeAnalysis(true);
        toast.success('Resume analyzed successfully!');
      }
    } catch (error) {
      toast.error('Failed to analyze resume');
      console.error('Resume analysis error:', error);
    } finally {
      setIsAnalyzingResume(false);
    }
  };

  // Start interview
  const startInterview = () => {
    dispatch({ type: 'SET_INTERVIEW_ACTIVE', payload: true });
    toast.success('Interview started!');
  };

  // End interview
  const endInterview = async () => {
    if (window.confirm('Are you sure you want to end the interview?')) {
      try {
        await api.endInterview();
        navigate('/results');
      } catch (error) {
        toast.error('Failed to end interview');
      }
    }
  };

  // Toggle video
  const toggleVideo = () => {
    setIsVideoEnabled(!isVideoEnabled);
    toast(isVideoEnabled ? 'Video disabled' : 'Video enabled', {
      icon: isVideoEnabled ? 'ðŸ“¹' : 'ðŸ“¹',
      style: {
        background: isVideoEnabled ? '#ef4444' : '#10b981',
        color: 'white',
      },
    });
  };

  // Toggle audio
  const toggleAudio = () => {
    setIsAudioEnabled(!isAudioEnabled);
    toast(isAudioEnabled ? 'Audio disabled' : 'Audio enabled', {
      icon: isAudioEnabled ? 'ðŸŽ¤' : 'ðŸŽ¤',
      style: {
        background: isAudioEnabled ? '#ef4444' : '#10b981',
        color: 'white',
      },
    });
  };

  // Toggle pause
  const togglePause = () => {
    setIsPaused(!isPaused);
    toast(isPaused ? 'Interview resumed' : 'Interview paused', {
      icon: isPaused ? 'â–¶ï¸' : 'â¸ï¸',
      style: {
        background: isPaused ? '#10b981' : '#f59e0b',
        color: 'white',
      },
    });
  };

  // Format time
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Get current question
  const currentQuestion = utils.getCurrentQuestion();

  // Get performance color
  const getPerformanceColor = (score) => {
    if (score >= 80) return 'text-green-600';
    if (score >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  // Analyze metrics and generate warnings
  const analyzeWarnings = useCallback(() => {
    const warnings = [];
    
    // First priority: Check if face detection is working
    if (!metrics.face_detected) {
      warnings.push({
        type: 'error',
        message: 'No face detected! Please position yourself in front of the camera.',
        icon: 'ðŸ‘¤',
        priority: 1
      });
      
      // If no face detected, don't show other warnings
      setWarningMessages(warnings);
      return;
    }
    
    // Second priority: Check if eyes are visible (basic face detection passed)
    if (metrics.face_detected && metrics.eyeContactPercentage === 0) {
      warnings.push({
        type: 'error',
        message: 'Eyes not visible! Please look directly at the camera.',
        icon: 'ðŸ‘ï¸',
        priority: 2
      });
    }
    
    // Third priority: Detailed analysis (only if face and eyes are detected)
    if (metrics.face_detected && metrics.eyeContactPercentage > 0) {
      // Check eye contact quality
      if (metrics.eyeContactPercentage < 30) {
        warnings.push({
          type: 'warning',
          message: 'Low eye contact detected. Try to look at the camera more.',
          icon: 'ðŸ‘ï¸',
          priority: 3
        });
      } else if (metrics.eyeContactPercentage < 50) {
        warnings.push({
          type: 'info',
          message: 'Eye contact could be improved. Focus on the camera.',
          icon: 'ðŸ‘ï¸',
          priority: 4
        });
      }
      
      // Check confidence score
      if (metrics.confidenceScore < 0.4) {
        warnings.push({
          type: 'warning',
          message: 'Low confidence detected. Try to sit up straight and maintain good posture.',
          icon: 'ðŸ’ª',
          priority: 3
        });
      }
      
      // Check speech clarity
      if (metrics.speechClarity < 0.6) {
        warnings.push({
          type: 'warning',
          message: 'Speech clarity needs improvement. Speak more clearly and slowly.',
          icon: 'ðŸ—£ï¸',
          priority: 3
        });
      }
      
      // Check for negative emotions
      if (metrics.emotionScores) {
        const negativeEmotions = ['angry', 'sad', 'fear', 'disgust'];
        negativeEmotions.forEach(emotion => {
          if (metrics.emotionScores[emotion] > 0.5) {
            warnings.push({
              type: 'warning',
              message: `Detected ${emotion} expression. Try to maintain a positive demeanor.`,
              icon: 'ðŸ˜Š',
              priority: 4
            });
          }
        });
      }
    }
    
    // Sort warnings by priority (1 = highest priority)
    warnings.sort((a, b) => a.priority - b.priority);
    setWarningMessages(warnings);
  }, [metrics]);

  // Get face detection status
  const getFaceStatus = () => {
    if (!metrics.face_detected) {
      return {
        status: 'no-face',
        text: 'No Face Detected',
        color: 'text-red-600',
        icon: 'ðŸ‘¤'
      };
    }
    
    if (metrics.eyeContactPercentage === 0) {
      return {
        status: 'no-eyes',
        text: 'Eyes Not Visible',
        color: 'text-orange-600',
        icon: 'ðŸ‘ï¸'
      };
    }
    
    return {
      status: 'face-detected',
      text: 'Face & Eyes Detected',
      color: 'text-green-600',
      icon: 'âœ…'
    };
  };

  const faceStatus = getFaceStatus();

  // Update warnings when metrics change
  useEffect(() => {
    analyzeWarnings();
  }, [analyzeWarnings]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-purple-600 to-indigo-700">
      <div className="container mx-auto px-4 py-6">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="text-white">
            <h1 className="text-2xl font-bold">Interview Session</h1>
            <p className="text-blue-100">Candidate: {candidateName}</p>
          </div>
          
                     <div className="flex items-center space-x-4">
             <div className="bg-white/10 backdrop-blur-sm rounded-lg px-4 py-2 text-white">
               <span className="text-sm">Time: {formatTime(interviewTime)}</span>
             </div>
             
             {/* Interview Status Indicator */}
             <div className={`px-3 py-2 rounded-lg backdrop-blur-sm flex items-center space-x-2 status-indicator ${
               warningMessages.length === 0 
                 ? 'bg-green-500/20 text-green-100' 
                 : warningMessages.some(w => w.type === 'error')
                 ? 'bg-red-500/20 text-red-100'
                 : 'bg-yellow-500/20 text-yellow-100'
             }`}>
               <div className={`w-2 h-2 rounded-full ${
                 warningMessages.length === 0 
                   ? 'bg-green-400' 
                   : warningMessages.some(w => w.type === 'error')
                   ? 'bg-red-400'
                   : 'bg-yellow-400'
               }`}></div>
               <span className="text-sm font-medium">
                 {warningMessages.length === 0 
                   ? 'All Good' 
                   : warningMessages.some(w => w.type === 'error')
                   ? 'Critical Issues'
                   : 'Needs Attention'
                 }
               </span>
             </div>
             
             {/* Face Detection Status Indicator */}
             <div className={`px-3 py-2 rounded-lg backdrop-blur-sm flex items-center space-x-2 ${faceStatus.bgColor} ${
                faceStatus.status === 'no-face' ? 'face-status-no-face' :
                faceStatus.status === 'no-eyes' ? 'face-status-no-eyes' :
                'face-status-detected'
              }`}>
                <span className="text-lg">{faceStatus.icon}</span>
                <span className={`text-sm font-medium ${faceStatus.color}`}>
                  {faceStatus.text}
                </span>
              </div>
              
              {/* Connection Status Indicator */}
              <div className={`px-3 py-2 rounded-lg backdrop-blur-sm flex items-center space-x-2 ${
                isConnected ? 'bg-green-500/20 text-green-100' : 'bg-red-500/20 text-red-100'
              }`}>
                <div className={`w-2 h-2 rounded-full ${
                  isConnected ? 'bg-green-400' : 'bg-red-400'
                }`}></div>
                <span className="text-sm font-medium">
                  {isConnected ? 'Connected' : 'Disconnected'}
                </span>
              </div>
            
            <div className="flex items-center space-x-2">
              <button
                onClick={togglePause}
                className="p-2 bg-white/10 backdrop-blur-sm rounded-lg text-white hover:bg-white/20 transition-colors"
              >
                {isPaused ? <Play className="w-5 h-5" /> : <Pause className="w-5 h-5" />}
              </button>
              
              <button
                onClick={endInterview}
                className="p-2 bg-red-500 rounded-lg text-white hover:bg-red-600 transition-colors"
              >
                <Square className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>

        {/* Eye Contact Warning Display */}
        {eyeContactWarning && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            className={`fixed top-4 left-1/2 transform -translate-x-1/2 z-50 p-4 rounded-lg shadow-lg max-w-md ${
              eyeContactStatus?.warning_level === 'high' 
                ? 'bg-red-500 text-white' 
                : 'bg-yellow-500 text-black'
            }`}
          >
            <div className="flex items-center space-x-2">
              <div className="text-2xl">âš ï¸</div>
              <div>
                <div className="font-semibold">
                  {eyeContactStatus?.warning_level === 'high' ? 'CRITICAL WARNING' : 'WARNING'}
                </div>
                <div className="text-sm opacity-90">{eyeContactWarning}</div>
                {eyeContactTimer > 0 && (
                  <div className="text-xs opacity-75 mt-1">
                    Time without eye contact: {eyeContactTimer}s
                  </div>
                )}
              </div>
            </div>
          </motion.div>
        )}

        {/* Eye Contact Status Indicator */}
        <div className="fixed top-4 right-4 z-40">
          <div className={`p-3 rounded-lg shadow-lg ${
            eyeContactStatus?.is_looking 
              ? 'bg-green-500 text-white' 
              : 'bg-red-500 text-white'
          }`}>
            <div className="flex items-center space-x-2">
              <div className={`w-3 h-3 rounded-full ${
                eyeContactStatus?.is_looking ? 'bg-green-300' : 'bg-red-300'
              }`}></div>
              <div className="text-sm font-medium">
                {eyeContactStatus?.is_looking ? 'Looking at Camera' : 'Not Looking'}
              </div>
            </div>
            {eyeContactStatus && (
              <div className="text-xs opacity-75 mt-1">
                Streak: {eyeContactStatus.current_streak}s | 
                Longest: {eyeContactStatus.longest_streak}s
              </div>
            )}
          </div>
        </div>

        {/* Main Content */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Main Interview Area */}
          <div className="lg:col-span-2 space-y-6">
                         {/* Webcam */}
             <div className="relative">
               <div className="webcam-container">
                 {isVideoEnabled ? (
                   <Webcam
                     ref={webcamRef}
                     audio={false}
                     screenshotFormat="image/jpeg"
                     className="w-full h-96 object-cover rounded-lg"
                   />
                 ) : (
                   <div className="w-full h-96 bg-gray-800 rounded-lg flex items-center justify-center">
                     <div className="text-center text-white">
                       <Camera className="w-16 h-16 mx-auto mb-4 opacity-50" />
                       <p>Camera Disabled</p>
                     </div>
                   </div>
                 )}
                 
                 {!isInterviewActive && (
                   <div className="webcam-overlay">
                     <div className="text-center">
                       <p className="mb-4">Ready to start your interview?</p>
                       <button
                         onClick={startInterview}
                         className="btn-primary"
                       >
                         Start Interview
                       </button>
                     </div>
                   </div>
                 )}

                 {/* Real-time Warnings Overlay */}
                 {isInterviewActive && showWarnings && warningMessages.length > 0 && (
                   <div className="absolute top-4 right-4 z-10 space-y-2 max-w-sm warning-overlay">
                     {warningMessages.slice(0, 3).map((warning, index) => (
                       <motion.div
                         key={index}
                         initial={{ opacity: 0, x: 100 }}
                         animate={{ opacity: 1, x: 0 }}
                         exit={{ opacity: 0, x: 100 }}
                         className={`p-3 rounded-lg backdrop-blur-sm border-l-4 shadow-lg warning-card ${
                           warning.type === 'error' 
                             ? 'bg-red-500/90 text-white border-red-600 critical-warning' 
                             : warning.type === 'warning'
                             ? 'bg-yellow-500/90 text-white border-yellow-600'
                             : 'bg-blue-500/90 text-white border-blue-600'
                         }`}
                       >
                         <div className="flex items-start space-x-2">
                           <span className="text-lg">{warning.icon}</span>
                           <div className="flex-1">
                             <p className="text-sm font-medium">{warning.message}</p>
                             <div className="flex items-center space-x-2 mt-1">
                               <span className={`text-xs px-2 py-1 rounded-full ${
                                 warning.priority === 1 ? 'bg-red-600/50' :
                                 warning.priority === 2 ? 'bg-orange-600/50' :
                                 warning.priority === 3 ? 'bg-yellow-600/50' :
                                 'bg-blue-600/50'
                               }`}>
                                 Priority {warning.priority}
                               </span>
                               <span className="text-xs opacity-75">
                                 {warning.type === 'error' ? 'Critical' : 
                                  warning.type === 'warning' ? 'Important' : 'Info'}
                               </span>
                             </div>
                           </div>
                         </div>
                       </motion.div>
                     ))}
                   </div>
                 )}
                 
                 {/* Face Detection Indicator */}
                 {isInterviewActive && (
                   <div className="absolute top-4 left-4 z-10">
                     <div className={`px-3 py-2 rounded-lg backdrop-blur-sm flex items-center space-x-2 ${
                       faceStatus.status === 'no-face' ? 'bg-red-500/90 text-white' :
                       faceStatus.status === 'no-eyes' ? 'bg-orange-500/90 text-white' :
                       'bg-green-500/90 text-white'
                     }`}>
                       <div className={`w-2 h-2 rounded-full ${
                         faceStatus.status === 'no-face' ? 'bg-red-200' :
                         faceStatus.status === 'no-eyes' ? 'bg-orange-200' :
                         'bg-green-200'
                       } ${isProcessing ? 'animate-pulse' : ''}`}></div>
                       <span className="text-sm font-medium">
                         {isProcessing ? 'Analyzing...' :
                          faceStatus.status === 'no-face' ? 'No Face' :
                          faceStatus.status === 'no-eyes' ? 'No Eyes' :
                          'Detecting'}
                       </span>
                       {isProcessing && (
                         <div className="w-3 h-3 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                       )}
                     </div>
                   </div>
                 )}
               </div>

              {/* Camera Controls */}
              <div className="absolute bottom-4 left-4 flex space-x-2">
                <button
                  onClick={toggleVideo}
                  className={`p-2 rounded-lg backdrop-blur-sm transition-colors ${
                    isVideoEnabled 
                      ? 'bg-white/20 text-white' 
                      : 'bg-red-500 text-white'
                  }`}
                >
                  <Camera className="w-5 h-5" />
                </button>
                
                <button
                  onClick={toggleAudio}
                  className={`p-2 rounded-lg backdrop-blur-sm transition-colors ${
                    isAudioEnabled 
                      ? 'bg-white/20 text-white' 
                      : 'bg-red-500 text-white'
                  }`}
                >
                  {isAudioEnabled ? <Mic className="w-5 h-5" /> : <MicOff className="w-5 h-5" />}
                </button>
              </div>
            </div>

            {/* Current Question */}
            {currentQuestion && (
              <motion.div
                key={currentQuestionIndex}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="question-card"
              >
                <div className="flex items-start justify-between mb-4">
                  <div>
                    <span className="text-sm opacity-75">Question {currentQuestionIndex + 1} of {questions.length}</span>
                    <div className="flex items-center space-x-2 mt-1">
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        currentQuestion.category === 'technical' ? 'bg-blue-500/20' :
                        currentQuestion.category === 'behavioral' ? 'bg-green-500/20' :
                        'bg-gray-500/20'
                      }`}>
                        {currentQuestion.category}
                      </span>
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        currentQuestion.difficulty === 'easy' ? 'bg-green-500/20' :
                        currentQuestion.difficulty === 'medium' ? 'bg-yellow-500/20' :
                        'bg-red-500/20'
                      }`}>
                        {currentQuestion.difficulty}
                      </span>
                    </div>
                  </div>
                </div>
                
                <h3 className="text-xl font-semibold mb-4">
                  {currentQuestion.question}
                </h3>

                {/* Answer Input */}
                {!followupQuestion && (
                  <div className="mb-4">
                    {showAnswerInput ? (
                      <>
                        <textarea
                          value={currentAnswer}
                          onChange={(e) => setCurrentAnswer(e.target.value)}
                          placeholder="Type your answer here... (Be detailed and provide examples)"
                          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none"
                          rows={4}
                          disabled={isSubmittingAnswer}
                        />
                        <div className="flex justify-between items-center mt-2">
                          <button
                            onClick={() => setShowAnswerInput(false)}
                            className="text-sm text-gray-500 hover:text-gray-700"
                          >
                            Hide Answer Input
                          </button>
                          <button
                            onClick={submitAnswer}
                            disabled={!currentAnswer.trim() || isSubmittingAnswer}
                            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
                          >
                            {isSubmittingAnswer ? (
                              <>
                                <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                                Submitting...
                              </>
                            ) : (
                              <>
                                <CheckCircle className="w-4 h-4 mr-2" />
                                Submit Answer
                              </>
                            )}
                          </button>
                        </div>
                      </>
                    ) : (
                      <div className="text-center py-4">
                        <button
                          onClick={() => setShowAnswerInput(true)}
                          className="px-4 py-2 bg-blue-100 text-blue-700 rounded-lg hover:bg-blue-200 transition-colors"
                        >
                          ðŸ“ Click to Answer This Question
                        </button>
                      </div>
                    )}
                  </div>
                )}

                {/* Follow-up Question */}
                {followupQuestion && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="mb-4 p-4 bg-blue-50 border border-blue-200 rounded-lg"
                  >
                    <h4 className="font-semibold text-blue-900 mb-2">ðŸ¤– AI Follow-up Question:</h4>
                    <p className="text-blue-800 mb-3">{followupQuestion.question}</p>
                    <div className="flex items-center space-x-2 text-xs text-blue-600">
                      <span className="px-2 py-1 bg-blue-100 rounded">
                        {followupQuestion.category}
                      </span>
                      <span className="px-2 py-1 bg-blue-100 rounded">
                        {followupQuestion.difficulty}
                      </span>
                    </div>
                  </motion.div>
                )}
                
                <div className="flex justify-between">
                  <button
                    onClick={() => utils.previousQuestion()}
                    disabled={currentQuestionIndex === 0}
                    className="flex items-center px-4 py-2 bg-white/20 rounded-lg text-white hover:bg-white/30 transition-colors disabled:opacity-50"
                  >
                    <SkipBack className="w-4 h-4 mr-2" />
                    Previous
                  </button>
                  
                  <button
                    onClick={nextQuestion}
                    disabled={currentQuestionIndex === questions.length - 1 && !followupQuestion}
                    className="flex items-center px-4 py-2 bg-white/20 rounded-lg text-white hover:bg-white/30 transition-colors disabled:opacity-50"
                  >
                    Next
                    <SkipForward className="w-4 h-4 ml-2" />
                  </button>
                </div>
              </motion.div>
            )}
          </div>

          {/* Metrics Panel */}
          <div className="space-y-6">
            {/* Resume Analysis Section */}
            <div className="metrics-card p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <Brain className="w-5 h-5 mr-2" />
                Resume Analysis
              </h3>
              
              {!showResumeAnalysis ? (
                <div className="space-y-3">
                  <div className="text-sm text-gray-600">
                    Upload your resume for AI-powered analysis
                  </div>
                  
                  <input
                    type="file"
                    accept=".pdf,.doc,.docx,.txt"
                    onChange={handleResumeUpload}
                    className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                  />
                  
                  <button
                    onClick={analyzeResume}
                    disabled={!resumeFile || isAnalyzingResume}
                    className="w-full px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
                  >
                    {isAnalyzingResume ? (
                      <>
                        <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                        Analyzing...
                      </>
                    ) : (
                      'Analyze Resume'
                    )}
                  </button>
                </div>
              ) : (
                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <span className="text-sm font-medium text-green-600">Analysis Complete</span>
                    <button
                      onClick={() => setShowResumeAnalysis(false)}
                      className="text-gray-400 hover:text-gray-600 text-sm"
                    >
                      <X className="w-4 h-4" />
                    </button>
                  </div>
                  
                  {resumeAnalysis && (
                    <div className="space-y-3 text-sm">
                      <div className="bg-blue-50 rounded-lg p-3">
                        <div className="text-center mb-2">
                          <div className="text-2xl font-bold text-blue-600">
                            {resumeAnalysis.overall_score}/100
                          </div>
                          <div className="text-blue-500 text-xs">Overall Score</div>
                        </div>
                      </div>
                      
                      <div className="space-y-2">
                        <div className="text-gray-700 font-medium">Skills Found:</div>
                        <div className="flex flex-wrap gap-1">
                          {resumeAnalysis.skills?.slice(0, 6).map((skill, index) => (
                            <span key={index} className="px-2 py-1 bg-blue-100 text-blue-700 text-xs rounded">
                              {skill}
                            </span>
                          ))}
                        </div>
                      </div>
                      
                      <div className="space-y-2">
                        <div className="text-gray-700 font-medium">Experience:</div>
                        <div className="text-gray-600 text-xs">
                          {resumeAnalysis.experience_years} years | {resumeAnalysis.education_level}
                        </div>
                      </div>
                      
                      <button
                        onClick={() => setShowResumeAnalysis(false)}
                        className="w-full px-3 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 text-sm"
                      >
                        View Full Analysis
                      </button>
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Face Detection Status */}
            <div className="metrics-card p-6">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                  <span className="text-2xl mr-2">{faceStatus.icon}</span>
                  Face Detection Status
                </h3>
                <div className="flex space-x-2">
                  <button
                    onClick={testFaceDetection}
                    className="px-3 py-1 text-xs bg-green-100 text-green-700 rounded hover:bg-green-200 transition-colors"
                  >
                    Test
                  </button>
                  <button
                    onClick={() => api.getCurrentMetrics(sessionId)}
                    className="px-3 py-1 text-xs bg-blue-100 text-blue-700 rounded hover:bg-blue-200 transition-colors"
                  >
                    Refresh
                  </button>
                </div>
              </div>
              
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-700">Status</span>
                  <span className={`text-sm font-semibold ${faceStatus.color}`}>
                    {faceStatus.text}
                  </span>
                </div>
                
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium text-gray-700">Face Detected</span>
                  <span className={`text-sm font-semibold ${
                    metrics.face_detected ? 'text-green-600' : 'text-red-600'
                  }`}>
                    {metrics.face_detected ? 'Yes' : 'No'}
                  </span>
                </div>
                
                {metrics.face_detected && (
                  <>
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium text-gray-700">Landmarks</span>
                      <span className="text-sm font-semibold text-gray-900">
                        {metrics.landmarks || 0}
                      </span>
                    </div>
                    
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium text-gray-700">Eye Contact</span>
                      <span className={`text-sm font-semibold ${
                        metrics.eyeContactPercentage > 0 ? 'text-green-600' : 'text-red-600'
                      }`}>
                        {metrics.eyeContactPercentage > 0 ? 'Detected' : 'Not Detected'}
                      </span>
                    </div>
                  </>
                )}
                
                <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                  <div 
                    className={`h-2 rounded-full transition-all duration-300 ${
                      faceStatus.status === 'face-detected' ? 'bg-green-600' :
                      faceStatus.status === 'no-eyes' ? 'bg-orange-600' :
                      'bg-red-600'
                    }`}
                    style={{ 
                      width: faceStatus.status === 'face-detected' ? '100%' :
                             faceStatus.status === 'no-eyes' ? '50%' : '0%'
                    }}
                  />
                </div>
                
                {/* Debug Information */}
                <div className="mt-4 p-3 bg-gray-100 rounded text-xs">
                  <strong>Debug Info:</strong><br/>
                  Face Detected: {String(metrics.face_detected)}<br/>
                  Eye Contact: {metrics.eyeContactPercentage}%<br/>
                  Confidence: {Math.round((metrics.confidenceScore || 0) * 100)}%<br/>
                  Landmarks: {metrics.landmarks || 0}<br/>
                  Processing: {String(isProcessing)}<br/>
                  WebSocket: {String(isConnected)}<br/>
                  Session ID: {sessionId || 'None'}
                </div>
              </div>
            </div>

            {/* Real-time Metrics */}
            <div className="metrics-card p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <BarChart3 className="w-5 h-5 mr-2" />
                Real-time Metrics
              </h3>
              
              <div className="space-y-4">
                {/* Eye Contact */}
                <div>
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm font-medium text-gray-700">Eye Contact</span>
                    <span className={`text-sm font-semibold ${getPerformanceColor(metrics.eyeContactPercentage)}`}>
                      {Math.round(metrics.eyeContactPercentage)}%
                    </span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${metrics.eyeContactPercentage}%` }}
                    />
                  </div>
                </div>

                {/* Confidence Score */}
                <div>
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm font-medium text-gray-700">Confidence</span>
                    <span className={`text-sm font-semibold ${getPerformanceColor(metrics.confidenceScore * 100)}`}>
                      {Math.round(metrics.confidenceScore * 100)}%
                    </span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-green-600 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${metrics.confidenceScore * 100}%` }}
                    />
                  </div>
                </div>

                {/* Speech Clarity */}
                <div>
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-sm font-medium text-gray-700">Speech Clarity</span>
                    <span className={`text-sm font-semibold ${getPerformanceColor(metrics.speechClarity * 100)}`}>
                      {Math.round(metrics.speechClarity * 100)}%
                    </span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2">
                    <div 
                      className="bg-purple-600 h-2 rounded-full transition-all duration-300"
                      style={{ width: `${metrics.speechClarity * 100}%` }}
                    />
                  </div>
                </div>
              </div>
            </div>

                         {/* Warnings */}
             <div className="metrics-card p-6">
               <div className="flex items-center justify-between mb-4">
                 <div className="flex items-center space-x-2">
                   <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                     <AlertTriangle className="w-5 h-5 mr-2 text-red-500" />
                     Live Warnings
                   </h3>
                   <div className="group relative">
                     <div className="w-4 h-4 bg-gray-300 rounded-full flex items-center justify-center cursor-help">
                       <span className="text-xs text-gray-600">?</span>
                     </div>
                     <div className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-2 bg-gray-800 text-white text-xs rounded-lg opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-20">
                       <div className="mb-1">
                         <span className="font-semibold">Priority System:</span>
                       </div>
                       <div>1: Critical (No face/eyes)</div>
                       <div>2: High (Basic issues)</div>
                       <div>3: Medium (Improvements needed)</div>
                       <div>4: Low (Minor suggestions)</div>
                       <div className="absolute top-full left-1/2 transform -translate-x-1/2 w-0 h-0 border-l-4 border-r-4 border-t-4 border-transparent border-t-gray-800"></div>
                     </div>
                   </div>
                 </div>
                 <button
                   onClick={() => setShowWarnings(!showWarnings)}
                   className={`px-3 py-1 text-xs rounded-full transition-colors ${
                     showWarnings 
                       ? 'bg-green-100 text-green-700' 
                       : 'bg-gray-100 text-gray-700'
                   }`}
                 >
                   {showWarnings ? 'ON' : 'OFF'}
                 </button>
               </div>
               
               {/* Warning Summary */}
               {warningMessages.length > 0 && (
                 <div className="mb-4 p-3 bg-gray-50 rounded-lg">
                   <div className="flex items-center justify-between mb-2">
                     <span className="text-sm font-medium text-gray-700">Warning Summary</span>
                     <span className="text-sm text-gray-500">{warningMessages.length} total</span>
                   </div>
                   <div className="flex space-x-2">
                     {warningMessages.some(w => w.priority === 1) && (
                       <span className="text-xs px-2 py-1 bg-red-100 text-red-700 rounded-full">
                         {warningMessages.filter(w => w.priority === 1).length} Critical
                       </span>
                     )}
                     {warningMessages.some(w => w.priority === 2) && (
                       <span className="text-xs px-2 py-1 bg-orange-100 text-orange-700 rounded-full">
                         {warningMessages.filter(w => w.priority === 2).length} High
                       </span>
                     )}
                     {warningMessages.some(w => w.priority === 3) && (
                       <span className="text-xs px-2 py-1 bg-yellow-100 text-yellow-700 rounded-full">
                         {warningMessages.filter(w => w.priority === 3).length} Medium
                       </span>
                     )}
                     {warningMessages.some(w => w.priority === 4) && (
                       <span className="text-xs px-2 py-1 bg-blue-100 text-blue-700 rounded-full">
                         {warningMessages.filter(w => w.priority === 4).length} Low
                       </span>
                     )}
                   </div>
                 </div>
               )}
               {warningMessages.length > 0 ? (
                 <div className="space-y-3">
                   {warningMessages.map((warning, index) => (
                     <div 
                       key={index} 
                       className={`flex items-start space-x-2 p-3 rounded-lg border-l-4 ${
                         warning.priority === 1 ? 'priority-1' :
                         warning.priority === 2 ? 'priority-2' :
                         warning.priority === 3 ? 'priority-3' :
                         'priority-4'
                       }`}
                     >
                       <span className="text-lg">{warning.icon}</span>
                       <div className="flex-1">
                         <p className={`text-sm font-medium ${
                           warning.type === 'error' 
                             ? 'text-red-800' 
                             : warning.type === 'warning'
                             ? 'text-yellow-800'
                             : 'text-blue-800'
                         }`}>
                           {warning.message}
                         </p>
                         <div className="flex items-center space-x-2 mt-2">
                           <span className={`text-xs px-2 py-1 rounded-full font-medium ${
                             warning.priority === 1 ? 'bg-red-200 text-red-800' :
                             warning.priority === 2 ? 'bg-orange-200 text-orange-800' :
                             warning.priority === 3 ? 'bg-yellow-200 text-yellow-800' :
                             'bg-blue-200 text-blue-800'
                           }`}>
                             Priority {warning.priority}
                           </span>
                           <span className={`text-xs px-2 py-1 rounded-full ${
                             warning.type === 'error' ? 'bg-red-100 text-red-700' : 
                             warning.type === 'warning' ? 'bg-yellow-100 text-yellow-700' : 
                             'bg-blue-100 text-blue-700'
                           }`}>
                             {warning.type === 'error' ? 'Critical' : 
                              warning.type === 'warning' ? 'Important' : 'Info'}
                           </span>
                         </div>
                       </div>
                     </div>
                   ))}
                 </div>
               ) : (
                 <div className="text-center py-4">
                   <CheckCircle className="w-8 h-8 text-green-500 mx-auto mb-2" />
                   <p className="text-sm text-gray-500">No warnings detected</p>
                   <p className="text-xs text-gray-400">Keep up the good work!</p>
                 </div>
               )}
             </div>

            {/* Emotion Analysis */}
            {metrics.emotionScores && Object.keys(metrics.emotionScores).length > 0 && (
              <div className="metrics-card p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                  <Brain className="w-5 h-5 mr-2" />
                  Emotion Analysis
                </h3>
                
                <div className="space-y-2">
                  {Object.entries(metrics.emotionScores).map(([emotion, score]) => (
                    <div key={emotion} className="flex items-center justify-between">
                      <span className="text-sm text-gray-700 capitalize">{emotion}</span>
                      <span className="text-sm font-semibold text-gray-900">
                        {Math.round(score * 100)}%
                      </span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Interview Progress */}
            <div className="metrics-card p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Progress</h3>
              
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-sm text-gray-700">Questions Completed</span>
                  <span className="text-sm font-semibold text-gray-900">
                    {currentQuestionIndex + 1} / {questions.length}
                  </span>
                </div>
                
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${((currentQuestionIndex + 1) / questions.length) * 100}%` }}
                  />
                </div>
                
                <div className="text-center">
                  <span className="text-xs text-gray-500">
                    {Math.round(((currentQuestionIndex + 1) / questions.length) * 100)}% Complete
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InterviewRoom;
